// api.js
import axios from 'axios';

const baseURL = 'http://localhost:5000/api'; // adjust this to match your backend URL

export const api = {
  getContacts: (page, limit, sortBy, order) =>
    axios.get(`${baseURL}/contacts`, {
      params: { page, limit, sortBy, order }
    }),
  
  createContact: (data) =>
    axios.post(`${baseURL}/contacts`, data),
  
  updateContact: (id, data) =>
    axios.put(`${baseURL}/contacts/${id}`, data),
  
  deleteContact: (id) =>
    axios.delete(`${baseURL}/contacts/${id}`)
};
