import React, { useState } from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  IconButton,
  TablePagination,
  TableSortLabel,
} from '@mui/material';
import { Edit as EditIcon, Delete as DeleteIcon } from 'lucide-react';

const ContactTable = ({
  contacts,
  totalPages,
  page,
  rowsPerPage,
  handleChangePage,
  handleChangeRowsPerPage,
  onEdit,
  onDelete,
  initialSortBy = 'firstName',
  initialOrder = 'asc'
}) => {
  const [sortBy, setSortBy] = useState(initialSortBy);
  const [order, setOrder] = useState(initialOrder);

  const handleSort = (column) => {
    const isAsc = sortBy === column && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setSortBy(column);
    
    // Sort the contacts array
    const sortedContacts = [...contacts].sort((a, b) => {
      if (a[column] < b[column]) return order === 'asc' ? -1 : 1;
      if (a[column] > b[column]) return order === 'asc' ? 1 : -1;
      return 0;
    });

    // You might want to lift this state up to the parent component
    // by passing the sorted data through a callback
    if (onSort) {
      onSort(sortedContacts, column, isAsc ? 'desc' : 'asc');
    }
  };

  const columns = [
    { id: 'firstName', label: 'First Name' },
    { id: 'lastName', label: 'Last Name' },
    { id: 'email', label: 'Email' },
    { id: 'phoneNumber', label: 'Phone Number' },
    { id: 'company', label: 'Company' },
    { id: 'jobTitle', label: 'Job Title' }
  ];

  return (
    <div className="w-full">
      <Paper className="w-full mb-4">
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                {columns.map((column) => (
                  <TableCell key={column.id}>
                    <TableSortLabel
                      active={sortBy === column.id}
                      direction={sortBy === column.id ? order : 'asc'}
                      onClick={() => handleSort(column.id)}
                      className="font-medium"
                    >
                      {column.label}
                    </TableSortLabel>
                  </TableCell>
                ))}
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {contacts.map((contact) => (
                <TableRow key={contact._id} className="hover:bg-gray-50">
                  {columns.map((column) => (
                    <TableCell key={`${contact._id}-${column.id}`} className="p-4">
                      {contact[column.id]}
                    </TableCell>
                  ))}
                  <TableCell className="p-4">
                    <div className="flex gap-2">
                      <IconButton 
                        onClick={() => onEdit(contact)}
                        className="text-blue-600 hover:text-blue-800"
                      >
                        <EditIcon className="w-4 h-4" />
                      </IconButton>
                      <IconButton 
                        onClick={() => onDelete(contact._id)}
                        className="text-red-600 hover:text-red-800"
                      >
                        <DeleteIcon className="w-4 h-4" />
                      </IconButton>
                    </div>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          component="div"
          count={totalPages * rowsPerPage}
          page={page - 1}
          onPageChange={(e, newPage) => handleChangePage(newPage + 1)}
          rowsPerPage={rowsPerPage}
          onRowsPerPageChange={handleChangeRowsPerPage}
          className="border-t"
        />
      </Paper>
    </div>
  );
};

export default ContactTable;
